def handle_length arr
    return arr.length()
end
# puts handle_length(journalist)

def shortest_handle arr
    return arr.min()
end
# puts shortest_handle(journalist)


def five_letters arr
    count = 0
    arr.each do |handle|
        if handle.length() == 6
            count += 1
        end
    end
    return count
end
# puts five_letters(journalist)

def is_capitalise arr
    count = 0
    arr.each do |handle|
        if handle[1] == handle[1].upcase()
            count += 1
        end
    end
    return count
end
# puts is_capitalise(journalist)

def sort_alphabetically arr
    return arr.sort()
end
# puts sort_alphabetically(journalist)

def sort_by_length arr
    return arr.sort_by { |handle| handle.length() }
end
# puts sort_by_length(journalist)

def pos_epenser arr
    arr.each_with_index do |handle, i|
        if handle == "@epenser"
            return i
        end
    end
end
# puts pos_epenser(journalist)

def count_per_length arr
    arr = sort_by_length(arr)
    count = Hash.new(0)

    arr.each do |handle|
        count[handle.length()] += 1
    end

    return count
end
# puts count_per_length(journalist)

def choice_handler choice, journalist
    if choice == 1
        puts "Le nombre de journaliste dans la liste est :"
        puts handle_length(journalist)
    elsif choice == 2
        puts "Le handle le plus court est :"
        puts shortest_handle(journalist)
    elsif choice == 3
        puts "Le nombre de handle qui sont de 5 lettres (sans le @) est :"
        puts five_letters(journalist)
    elsif choice == 4
        puts "Le nombre de handle qui commencent par une majuscule est :"
        puts is_capitalise(journalist)
    elsif choice == 5
        puts "Voici trié par ordre alphabétique :"
        puts sort_alphabetically(journalist)
    elsif choice == 6
        puts "Voici trié par longueur :"
        puts sort_by_length(journalist)
    elsif choice == 7
        puts "L'index de l'handle @epenser est :"
        puts pos_epenser(journalist)
    elsif choice == 8
        puts "Le nombre de handle par taille est :"
        puts count_per_length(journalist)
    end
end

def choose journalist
    puts "Bonjour, bienvenue sur le serveur vocal (à l'écrit) de Twitter_Journalists"
    puts "Merci de patienter, nous allons prendre votre appel"
    puts ""
    puts "# 6 heures plus tard #"
    puts ""
    puts "Voici les options qui se présentent à vous :"
    puts "1. Pour connaître le nombre de journaliste dans la liste : tappez 1"
    puts "2. Le handle le plus court : tappez 2"
    puts "3. Le nombre de handle qui sont de 5 lettres (sans le @) : tappez 3"
    puts "4. Le nombre de handle qui commence par une majuscule : tappez 4"
    puts "5. Pour trier les handles par ordre alphabétique : tappez 5"
    puts "6. Pour trier les handles par longueur : tappez 6"
    puts "7. Pour obtenir l'index de l'handle @epenser : tappez 7"
    puts "8. Pour obtenir le nombre de handle par taille : tappez 8"
    puts "9. Pour tout avoir en mode YOLO : tappez 9"
    puts "10. Trop dur de choisir ? Pour une fonction random : tappez 10"
    puts ""
    print "> "
    choice = gets.chomp.to_i

    while choice <= 0 || choice > 10
        puts "Veuillez saisir un nombre entre 1 et 10"
        print "> "
        choice = gets.chomp.to_i
    end

    if choice == 9
        (1..8).each do |i|
            choice_handler(i, journalist)
        end
    elsif choice == 10
        choice_handler(rand(1..8), journalist)
    else
        choice_handler(choice, journalist)
    end
end 
choose(journalist)